function openNavBox(a){var b=document.getElementById("nav_box").style.display;if("none"!=b)document.getElementById("nav_box").style.display="none";else{document.getElementById("nav_box").style.display="block";var c=Date.now(),d=setInterval(function(){var a=-1e3+(Date.now()-c);document.getElementById("nav_box").style.left=(a/5).toFixed(0)+"px",a>0&&clearInterval(d)},20)}}